import React, {Fragment, useState} from 'react'
import { useFirestoreConnect } from "react-redux-firebase";
import ChatMessage from './ChatMessage'
import { connect } from 'react-redux'
import { useFirebase } from 'react-redux-firebase'
import { sendMessage } from '../store/actions/chatActions'

const ChatRoom = ({ messages, send }) => {

    const [input, setInput] = useState('');

    useFirestoreConnect([
        { collection: 'messages', orderBy: ['createAt', 'asc']}
    ])

    const auth = useFirebase().auth();

    const list = messages && messages.map(message => {
        return (
            <ChatMessage key={message.id} content={message.content} status={auth.currentUser === message.uid ? 'sent': 'received'}/>
        );
    })

    const handleSubmit = e => {
        e.preventDefault();
        if (auth.currentUser)
            send(input);
    }

    return (
        <Fragment>
            <h1>ChatRoom</h1>
            {list}
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    onChange={(e) => {
                        setInput(e.target.value)
                    }}
                    value={input}
                />
                <button disabled={!auth.currentUser}>click me</button>
            </form>
        </Fragment>
    );
}

const mapStateToProps = (state) => {
    return {
        messages: state.firestore.ordered.messages,
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        send: (message) => dispatch(sendMessage(message))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ChatRoom)
