const initialState =  {
    authError: '',
}

const authReducer = ( state = initialState, action) => {
    switch (action.type) {
        case 'LOGIN_SUCCESS':
        return state;
        case 'LOGIN_ERROR':
            return state;
    }
}

export default authReducer;
