import React, { useState, useRef} from 'react'
import { useFirestoreConnect } from "react-redux-firebase";
import ChatMessage from './ChatMessage'
import { connect } from 'react-redux'
import { useFirebase } from 'react-redux-firebase'
import NewMessageFrom from "./NewMessageForm";
import styled from '@emotion/styled'


const MessageBox = styled.div`
    padding: 20px;
`

const Container = styled.div`
    display: flex;
    flex-direction: column;
    background: red;
    height: 80vh;
`

const Warning = styled.div`
    display: flex;
    flex-direction: column wrap;
    align-items: flex-end;
    color: white;
    justify-content: center;
    border: 1px solid;
    flex: 1;
    positon: fixed;
    & > p {
        background-color: #ccc;
        border-radius: 25px;
        padding: 15px;
        font-size: 0.7em;
    }
`

const ChatRoom = ({ messages, isIn }) => {

    useFirestoreConnect([
        { collection: 'messages', orderBy: ['createAt', 'asc']}
    ])

    const auth = useFirebase().auth();

    const list = messages && messages.map(message => {
        return (
            <ChatMessage key={message.id} content={message.content} photoUrl={message.photoURL} status={auth.currentUser.uid === message.uid ? 'sent': 'received'}/>
        );
    })

    return (
        <Container>
            <MessageBox>
                {list}
            </MessageBox>
            { isIn ? <p/> : <Warning><div><p>You need to sign in first!</p></div></Warning>}
            <NewMessageFrom/>
        </Container>
    );
}

const mapStateToProps = (state) => {
    return {
        messages: state.firestore.ordered.messages,
        isIn: state.auth.isAuthenticated,
    }
}


export default connect(mapStateToProps)(ChatRoom)
