const initialState =  {
    authError: '',
    isAuthenticated: false,
}

const authReducer = ( state = initialState, action) => {
    switch (action.type) {
        case 'LOGIN_SUCCESS':
            return {
                ...state,
                authError: 'hello '+ action.user,
                isAuthenticated: true,
            };
        case 'LOGIN_ERROR':
            return {
                ...state,
                authError: 'Login failed',
            };
        case 'LOGOUT_SUCCESS':
            return {
                ...state,
                authError: null,
            };
        case 'LOGOUT_ERROR':
            return {
                ...state,
                authError: 'Logout failed',
            };
        default:
            return state;
    }
}

export default authReducer;
