import { connect } from 'react-redux'

const App = ({ auth }) => {
    console.log(auth)
    const isIn = auth.isAuthenticated;
    return (
        <div className={'App'}>
            <header>
            </header>

            <section>
                <ChatRoom />
            </section>
        </div>
