import React, {Fragment, useState, useRef} from 'react'
import { useFirestoreConnect } from "react-redux-firebase";
import ChatMessage from './ChatMessage'
import { connect } from 'react-redux'
import { useFirebase } from 'react-redux-firebase'
import { sendMessage } from '../store/actions/chatActions'

const ChatRoom = ({ messages, send }) => {
    const marker = useRef();
    const [input, setInput] = useState('');

    useFirestoreConnect([
        { collection: 'messages', orderBy: ['createAt', 'asc']}
    ])

    const auth = useFirebase().auth();

    const list = messages && messages.map(message => {
        return (
            <ChatMessage key={message.id} content={message.content} photoUrl={message.photoURL} status={auth.currentUser.uid === message.uid ? 'sent': 'received'}/>
        );
    })

    const handleSubmit = e => {
        e.preventDefault();
        if (auth.currentUser) {
            send(input);
            setInput('');
        }
        marker.current
    }

    return (
        <Fragment>
            <div className={'container'}>
                <div className="title">
                    <h1>ðŸ’¬</h1>
                </div>
                <div className="message-room">
                    <div className="wrapper">
                        {list}
                    </div>
                    <div ref={marker}/>
                </div>
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        onChange={(e) => {
                            setInput(e.target.value)
                        }}
                        value={input}
                    />
                    <button disabled={!input}>âž¡</button>
                </form>
            </div>

        </Fragment>
    );
}

const mapStateToProps = (state) => {
    return {
        messages: state.firestore.ordered.messages,
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        send: (message) => dispatch(sendMessage(message))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ChatRoom)
