import React, { useState, useRef} from 'react'
import { useFirestoreConnect } from "react-redux-firebase";
import ChatMessage from './ChatMessage'
import { connect } from 'react-redux'
import { useFirebase } from 'react-redux-firebase'
import NewMessageFrom from "./NewMessageForm";
import styled from '@emotion/styled'

const MessageBox = styled.div`
    padding: 20px;
    display: flex;
    flex-direction: column;
`

const Container = styled.div`
    display: flex;
    flex-flow: column wrap;
    min-height: 100vh;
`

const Warning = styled.div`
    display: ${props => props.isIn ? 'none' : 'flex'};
    color: white;
    justify-content: center;
    width: 100%;
    position: fixed;
    bottom: 10vh;
    & > p {
        background-color: #bbb;
        border-radius: 25px;
        padding: 15px;
        opacity: 0.8;
        font-size: 0.7em;
    }
`

const Marker = styled.a`
    height: 12vh;
    border: 1px solid white;
    color: white;
`

const ChatRoom = ({ messages, isIn }) => {
    useFirestoreConnect([
        { collection: 'messages', orderBy: ['createAt', 'asc']}
    ])

    const auth = useFirebase().auth();

    const list = messages && messages.map(message => {
        return (
            <ChatMessage
                key={message.id}
                content={message.content}
                photoUrl={message.photoURL}
                status={auth.currentUser === null || auth.currentUser.uid !== message.uid ? 'received' : 'sent'}
            />
        );
    })

    const marker = useRef();

    return (
        <>
            <Container>
                <Warning isIn={isIn}><p>You need to sign in first!</p></Warning>
                <MessageBox>
                    {list}
                    <Marker ref={marker}>They cant see me</Marker>
                </MessageBox>
                <NewMessageFrom marker={marker}/>
            </Container>
        </>
    );
}

const mapStateToProps = (state) => {
    return {
        messages: state.firestore.ordered.messages,
        isIn: state.auth.isAuthenticated,
    }
}


export default connect(mapStateToProps)(ChatRoom)
