const initialData = {
    output: '',
}

const chatReducer = (state = initialData, action) => {
    switch (action.type) {
        case 'MESSAGE_SENT':
            return {
                ...state,
                output: null,
            }

        default:
            return state;
    }
}

export default chatReducer;
